import * as React from 'react';
import { Image, Text, View, ScrollView } from 'react-native';
import { SafeAreaView } from "react-native-safe-area-context";

// Import helper code
import Settings from '../constants/Settings';

// Import styling and components
import Styles from "../styles/MainStyle";
import { MyButton } from '../components/MyButton';
import { TextH1, TextParagraph } from "../components/StyledText";
import Colours from '../constants/Colours';


export default function HomeScreen(props) {

  function showHelp() {
    props.navigation.replace('Root', {screen: 'Help'});
  }

  return (
    <SafeAreaView style={Styles.safeAreaView}>
      <ScrollView style={Styles.container} contentContainerStyle={Styles.contentContainer}>

        {/* Logo */}

        {/* Heading */}
        <View style={Styles.homeHeadingContainer}>
          <Text style={Styles.homeHeading}>ROI HR Management System</Text>
        </View>

        {/* Button */}
        <View style={{alignItems: 'center', justifyContent: 'center', marginVertical: 40}}>
          <MyButton
            text="View people"
            type="major"
            size="large"
            onPress={showHelp}
            buttonStyles = {Styles.homeButton}
          />
          <MyButton
            text="Show help screen"
            type="default"    // default*|major|minor
            size="large"      // small|medium*|large
            onPress={showHelp}
            buttonStyles = {Styles.homeButton}
          />
        </View>
      </ScrollView>
    </SafeAreaView>
  );
}
